import axios from "axios";
// import APIKEY from "../config/keys";
const KEY =
  process.env.GOOGLE_BOOK_API || "AIzaSyC--Ap_sGnu-HvqvWuZSKxFssWIArOMvUU";
// const KEY = "https://www.googleapis.com/books/v1/volumes?q=";

//   searchBook: function(term) {
//     console.log(term);
//     return axios.get(URL + term + APIKEY);
//   },

export default {
  // Gets all books
  getBooks: function() {
    return axios.get("/api/books");
  },
  // Gets the book with the given id
  getBook: function(id) {
    return axios.get("/api/books/" + id);
  },
  // Deletes the book with the given id
  deleteBook: function(id) {
    return axios.delete("/api/books/" + id);
  },
  // Saves a book to the database
  saveBook: function(bookData) {
    return axios.post("/api/books", bookData);
  },
  searchBook: function(term) {
    // return axios.get("/api/search/" + term);
    return axios.get(
      "https://www.googleapis.com/books/v1/volumes?q=" + term + "&key=" + KEY
    );
  }
};
